import { styled } from "styled-components"


const Container = styled.div`
    width: 100vw;
    height: 100vh;
    background: linear-gradient(
        rgba(255, 255, 255, 0.5),
        rgba(255, 255, 255, 0.5)), 
        url("https://images.yourstory.com/cs/7/1da9ec3014cc11e9a1b2b928167b6c62/mensfashionbanner1572434751640png?w=1152&fm=auto&ar=2:1&mode=crop&crop=faces")center;
        background-size: cover;
    display: flex;
    align-items: center;    
    justify-content: center;
`;

const Wrapper  = styled.div`
    width: 40%;
    padding: 20px;
    background-color: white;

`;

const Title = styled.h1`
    font-size: 24px;
    font-weight: 300;
`;

const Form = styled.form`
    display: flex;
    flex-wrap: wrap;
`;

const Input = styled.input`
    flex: 1;
    min-width: 40%;
    margin: 20px 10px 0px 0px;
    padding: 12px;
`;


const Button = styled.button`
    width: 40%;
    border: none;
    padding: 15px 20px;
    background-color: teal;
    color: white;
    cursor: pointer;
`;

const Link = styled.a``;


export const Login = () => {
  return (
    <Container>
        <Wrapper>
            <Title> SIGN IN </Title>
            <Form>
                <Input placeholder = "username"/>
                <Input placeholder = "password"/>
                <Button>LOGIN</Button>
                <Link>DO NOT REMEMBER YOUR PASSWORD?</Link>
                <Link>CREATE A NEW ACCOUNT</Link>
            </Form>
        </Wrapper>
    </Container>
  )
}
