import { ArrowLeftOutlined, ArrowRightOutlined } from "@mui/icons-material"
import { styled } from "styled-components"

const Container = styled.div`

    width: 100%;
    height: 100vh;
    display: flex;
    position: relative;
`;

const Wrapper = styled.div`
    height: 100%;
`;

const Slide = styled.div`
display: flex;
align-items:center;
`;

const ImgContainer = styled.div`
flex: 1;
`;

const Image = styled.img`

`
const InfoContainer = styled.div`
flex: 1;
`


const Arrow = styled.div`
    width: 50px;
    height: 50px;
    background-color: #fff7f7;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: ${props => props.direction === "left" && "10px"};
    right: ${props => props.direction === "right" && "10px"};
    margin: auto;
    cursor: pointer;
    opacity: 0.5;
`;

export const Slider = () => {
    return (
        <Container>
            <Arrow direction="left">
                <ArrowLeftOutlined />
            </Arrow>
            <Wrapper>
                <ImgContainer>
                    <Image src = "https://assets.vogue.com/photos/64a72dd2dc90cc14d7b90fc6/master/w_2560%2Cc_limit/image%2520(5).png"/>
                </ImgContainer>
                <InfoContainer></InfoContainer>

            </Wrapper>
            <Arrow direction="right">
                <ArrowRightOutlined />
            </Arrow>
        </Container>
    )
}
