import  styled  from "styled-components"

const Container = styled.div`
flex:1;
margin: 3px;
height: 70vh;
position: relative;

`;
const Image = styled.img`
    width: 100%;
    height: 100%;
    object-fit: cover;   // obeject fit crop the images...
    ${mobile({height:"50px"})}
`;
const Info = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`;
const Title = styled.h1`
    color: white;
    margin: 20px;
`;
const Button = styled.button`
    border: none;
    padding: 10px;
    backgroung-color: white;
    color: gray;
    cursor: pointer;
    font-weight: 600;
`;


export const CategoryItem = ({item}) => {
  return (
    <Container>
        <Image src = {item.img}/>
        <Info>
            <Title>{item.title}</Title>
            <Button>SHOP NOW</Button>
        </Info>
    </Container>
  )
}
